function getCountofCoursePerCountry($country)
{
    // $countries = unserialize($data);
    return Course::where('countries', 'like', '%' . $country . '%')->count();
}
function getLatCountry($id)
{
    if ($id == '0') {
        return null;
    }
    $countries = Country::where('code', $id)->first();
    if ($countries) {
        return $countries['lat'];
    }
    return null;
}

function getLngCountry($id)
{
    if ($id == '0') {
        return null;
    }
    $countries = Country::where('code', $id)->first();
    if ($countries) {
        return $countries['lat'];
    }
    return null;
}

function getRequest($key)
{
    if (isset($_GET[$key]) && $_GET[$key] != null) {
        return $_GET[$key];
    }

    return null;
}

function filterDatatableGet($get)
{
    $data = [];
    foreach ($get as $key => $value) {
        $data[str_replace('amp;', '', $key)] = $value;
    }

    return $data;
}

function getTrialField($id)
{
    $field = \App\Models\TrialTeralField::find($id);

    if (empty($field)) {
        return 'N\A';
    }

    return $field->name_ar;
}

function getSupplier($id)
{
    $supplier = \App\Models\AidSupplier::find($id);

    if (empty($supplier)) {
        return 'N\A';
    }

    return $supplier->name;
}


